window.addEventListener('load', function () {
    var timestamps = document.querySelector('.sub-menu .timestamps select');
    var results    = document.querySelector('.sub-menu .results select');

    function loadSelectPage (e) {
        location.search = e.target.value;
    }

    results.addEventListener('change', loadSelectPage);
    timestamps.addEventListener('change', loadSelectPage);

    $('.sub-menu .build-info').click(function () {
        if (!/disable/.test(this.className))
            $('#dialog').dialog('open');
    });

    $('#dialog').dialog({
        autoOpen:  false,
        width:     900,
        maxHeight: 600,
        show:      {
            effect:   'blind',
            duration: 200
        },
        hide:      {
            effect:   'blind',
            duration: 200
        }
    });
}, false);
